mapper.select:
, 지점순서 AS ( SELECT 지점, 순서 FROM J_참여자관리_지점 UNION ALL SELECT '평균' AS 지점, 0 AS 순서 ), 전체점수데이터 AS ( SELECT A.지점, A.이름, A.전담자_계정, A.총점 FROM DynamicViewResult A INNER JOIN J_참여자관리_로그인정보 L ON A.전담자_계정 = L.아이디 AND (아이디사용여부 NOT IN ('퇴사','정지','잠금') OR 아이디사용여부 IS NULL OR 아이디사용여부 = '')
WHERE L.근속기간구분 <> '1년미만'
), 총점및인원데이터 AS ( SELECT 지점, SUM(총점) AS 지점_총점, COUNT(이름) AS 지점_전담자수 FROM 전체점수데이터 GROUP BY 지점 ), 전체통계 AS ( SELECT 지점, 지점_총점, NULLIF(지점_전담자수, 0) AS 지점_전담자수 -- ISNULL(ROUND(지점_총점/NULLIF(지점_전담자수, 0), 2), 0) AS 지점평균 FROM 총점및인원데이터 UNION ALL SELECT '평균' AS 지점, SUM(지점_총점), NULLIF(SUM(지점_전담자수), 0) AS 지점_전담자수 -- ISNULL(ROUND(SUM(지점_총점)/NULLIF(SUM(지점_전담자수), 0), 2), 0) AS 지점평균 FROM 총점및인원데이터 ) SELECT T.지점 dashboardBranch, ISNULL(ROUND(T.지점_총점/NULLIF(T.지점_전담자수, 0), 2), 0) AS totalBranchScoreAVG -- ISNULL(T.지점평균, 0) AS totalBranchScoreAVG FROM 전체통계 T JOIN 지점순서 S ON T.지점 = S.지점 ORDER BY CASE WHEN T.지점 = '평균' THEN 0 ELSE 1 END, -- T.지점평균 DESC ISNULL(ROUND(T.지점_총점/NULLIF(T.지점_전담자수, 0), 2), 0) DESC
